sections.code:
::-webkit-scrollbar,
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track-piece {
  background: #1d2021 !important;    
}

::-webkit-scrollbar {
  width: 10px !important;
  height: 17px !important;
}

::-webkit-scrollbar-thumb {
  background: #3c3836;
  border: 3px solid #1d2021 !important;
  border-radius: 1px !important;
}

::-webkit-scrollbar-thumb:hover {
  background: #3c3836;
}

/** Original colors
  white-1 - fff
  white-2 - f6f8fa
  white-3 - e1e4e8
  white-4 - d1d5da
  white-5 - eaecef
  white-6 - dfe2e5
  white-7 - ccc
  black-1 - 24292e
  black-2 - 6a737d
  black-3 - 586069
  black-4 - 444d56
  blue-1 - 0366d6
  blue-2 - eaf5ff
  blue-3 - f1f8ff
  blue-4 - def
*/

:root {
  --white-1: #1e2022;
  --white-2: #303234;
  --white-3: #454647;
  --white-4: #616161;
  --white-5: #3a3c3d;
  --white-6: #524f4f;
  --white-7: #767676;
  --black-1: #dfdfdf;
  --black-2: #b2b6bb;
  --black-3: #d1d5da;
  --black-3: #b5bac1;
  --black-4: #969a9f;
  --blue-1: #559cf8;
  --blue-2: #27364a;
  --blue-3: #263242;
  --blue-4: #34465f;
  --blue-5: #405675;

  --box-shadow: #5d5d5d26;

  /* Syntax highlighting */
  --s-background: #282c34;
  --s-context: #2c313a;

  --s-chalky: #e5c07b;
  --s-coral: #e06c75;
  --s-cyan: #56b6c2;
  --s-error: #f44747;
  --s-ivory: #abb2bf;
  --s-malibu: #61afef;
  --s-sage: #98c379;
  --s-stone: #5c6370;
  --s-violet: #c678dd;
  --s-whiskey: #d19a66;
}

body {
  background-color: var(--white-1);
  color: var(--black-1);
}

.rule, hr {
  border-color: var(--white-6);
}

.form-hr {
  border-color: var(--white-5);
}

kbd {
  background-color: var(--white-2);
  border-color: var(--white-4);
  box-shadow: inset 0 -1px 0 var(--white-4);
  color: var(--black-4);
}

/** Backgrounds */
.bg-white {
  background-color: var(--white-1) !important;
}

.bg-gray,
.bg-gray-light {
  background-color: var(--white-2) !important;
}

.bg-gray-2 {
  background-color: var(--white-3) !important;
}

.bg-blue {
  background-color: var(--blue-1) !important;
}

.bg-blue-light {
  background-color: var(--blue-3) !important;
}

/** Links */
.link-gray-dark {
  color: var(--black-1) !important;
}

.muted-link,
.link-gray {
  color: var(--black-3) !important;
}

a,
.btn-link,
.btn-octicon:hover {
  color: var(--blue-1);
}

.muted-link:hover,
.link-gray:hover,
.link-gray-dark:hover,
.link-hover-blue:hover {
  color: var(--blue-1) !important;
}

/** Text */
.text-emphasized {
  color: var(--black-1);
}

.text-gray-light {
  color: var(--black-2) !important;
}

.color-gray-9,
.text-gray-dark {
  color: var(--black-1) !important;
}

.text-gray,
.note {
  color: var(--black-3) !important;
}

.bg-blue-2 {
  color: var(--white-1);
}

.text-blue {
  color: var(--blue-1) !important;
}

/** Borders */
.border,
.border-bottom,
.border-right,
.border-top,
.border-left {
   border-color: var(--white-3) !important;
}

.border-gray-dark {
  border-color: var(--white-4) !important;
}

.border-gray-light {
  border-color: var(--white-5) !important;
}

@media (min-width: 1012px) {
  .border-lg-left {
    border-color: var(--white-3) !important
  }
}

@media (min-width: 768px) {
  .border-md,
  .border-md-top {
    border-color: var(--white-3) !important;
  }
}

/** Buttons */
.btn:not(.btn-primary) {
  border-color: var(--white-6);
}

.btn:not(.btn-primary):not(.btn-outline) {
  background-color: var(--white-3);
  background-image: linear-gradient(-180deg, #696464, var(--white-6) 90%);
  color: var(--black-1);
}

.btn:not(.btn-primary):not(.btn-outline).hover,
.btn:not(.btn-primary):not(.btn-outline):hover {
  background-color: var(--white-4);
  background-image: linear-gradient(-180deg, #635e5e, #4d4b4b 90%);
  border-color: var(--white-4);
}

.btn-outline {
  background-color: var(--white-1);
  color: var(--blue-1);
}

.btn-outline.disabled,
.btn-outline:disabled,
.btn-outline[aria-disabled=true] {
  background-color: var(--white-1);
  border-color: var(--white-3) !important;
  color: var(--black-4);
}

.btn-outline.selected,
.btn-outline:active,
.btn-outline:hover,
.btn-outline[aria-selected=true],
[open] > .btn-outline {
  border-color: #0366d6;
}

.btn-danger {
  background-image: linear-gradient(-180deg, #cb2431, #941a24 90%) !important;
  border-color: #b31a26 !important;
  color: #fff !important;
}

.btn-danger:hover {
  background-color: #cb2431;
  background-image: linear-gradient(-180deg, #de4450, #cb2431 90%) !important;
}

/** Forms */
::placeholder {
  color: var(--black-2);
}

.form-control,
.form-select {
  background-color: var(--white-1);
  border-color: var(--white-4);
  color: var(--black-1);
}

.form-group .form-control,
.input-contrast {
  background-color: var(--white-2);
}

.form-group .form-control:focus,
.input-contrast:focus {
  background-color: var(--white-1);
}

/** Header */
.pagehead {
  border-color: var(--white-3);
}

.gh-header {
  background-color: var(--white-1);
}

.gh-header-meta {
  border-color: var(--white-3);
  color: var(--black-3);
}

.gh-header-number {
  color: var(--black-2);
}

.Subhead {
  border-color: var(--white-3);
}

.repohead h1 {
  color: var(--black-3);
}

.Header-link {
  color: #fff;
}

.Counter {
  background-color: #fff2;
  color: var(--black-3);
}

.breadcrumb strong.final-path {
  color: var(--black-1);
}

.pagehead-actions > li {
  color: var(--black-1);
}

/** Boxes */
.Box {
  background-color: var(--white-1);
  border-color: var(--white-4);
}

.Box-header {
  background-color: var(--white-2);
  border-color: var(--white-4);
}

.Box-header--blue {
  background-color: var(--blue-3);
  border-color: var(--blue-4);
}

.Box-row--blue {
  background-color: var(--blue-3);
}

.Box-row {
  border-color: var(--white-3);
}

.Box-row--hover-blue:hover {
  background-color: var(--blue-3);
}

.Box-row--focus-gray.navigation-focus {
  background-color: var(--white-2);
}

.Box-body {
  border-color: var(--white-3);
}

.Box-footer {
  border-color: var(--white-3);
}

.Box--danger,
.Box--danger .Box-body:last-of-type,
.Box--danger .Box-row:first-of-type {
  border-color: #d73a49;
}

/** Tables */
.table-list-header {
  background-color: var(--white-2);
  border-color: var(--white-3);
}

.table-list-header-toggle .btn-link:hover,
.table-list-header-toggle .btn-link.selected,
.table-list-header-toggle .btn-link.selected:hover {
  color: var(--black-1);
}

.table-list-header-toggle .btn-link {
  color: var(--black-3);
}

.table-list {
  border-color: var(--white-3);
  color: var(--black-2);
}

/** Dashboard activity */
.AvatarStack-body {
  background-color: var(--white-1);
}

.AvatarStack--right .avatar {
  border-color: var(--white-1);
}

.news div.message,
.news li blockquote {
  color: var(--black-3);
}

.dashboard-rollup-items > .dashboard-rollup-item {
  border-color: var(--white-3);
}

.commit-desc pre {
  color: var(--black-4);
}

.team-mention,
.user-mention {
  color: var(--black-1);
}

/** Popovers */
.SelectMenu-modal {
  background-color: var(--white-2);
}

@media (min-width: 544px) {
  .SelectMenu-modal {
    border-color: var(--white-4);
  }
}

.SelectMenu-list {
  background-color: var(--white-1);
  border-color: var(--white-3);
}

.SelectMenu-item {
  background-color: var(--white-1);
  border-color: var(--white-3);
  color: var(--black-3);
}

.SelectMenu-item[aria-checked=true] {
  color: var(--black-1);
}

@media (hover: hover) {
  .SelectMenu-item:hover,
  body:not(.intent-mouse) .SelectMenu-item:focus {
    background-color: var(--white-2);
  }
}

.SelectMenu-divider {
  background-color: var(--white-2);
  color: var(--black-2);
  border-color: var(--white-5);
}

.SelectMenu-filter {
  border-color: var(--white-3);
}

.SelectMenu-footer {
  border-color: var(--white-3);
  color: var(--black-2);
}

.SelectMenu-tab {
  color: var(--black-2);
}

.SelectMenu-tab[aria-selected=true] {
  background-color: var(--white-1);
  color: var(--black-1);
}

@media (min-width: 544px) {
  .SelectMenu-tab[aria-selected=true] {
    border-color: var(--white-3);
  }
}

@media (hover: hover) {
  .SelectMenu-tab:not([aria-checked=true]):active,
  .SelectMenu-tab:not([aria-checked=true]):hover {
    background-color: var(--white-1);
  }

  .SelectMenu-tab:not([aria-selected=true]):hover {
    background-color: var(--white-3);
    color: var(--black-1);
  }
}

.SelectMenu-blankslate,
.SelectMenu-loading,
.SelectMenu-message {
  background-color: var(--white-1);
}

.SelectMenu-message {
  border-color: var(--white-5);
}

/** Popover */
.Popover-message:after {
  border-bottom-color: var(--white-1);
}

.Popover-message:before {
  border-bottom-color: var(--white-4);
}

.Popover-message--bottom-left:after,
.Popover-message--bottom-left:before,
.Popover-message--bottom-right:after,
.Popover-message--bottom-right:before,
.Popover-message--bottom:after,
.Popover-message--bottom:before {
  border-bottom-color: transparent;
}

.Popover-message--bottom-left:after,
.Popover-message--bottom-right:after,
.Popover-message--bottom:after {
  border-top-color: var(--white-1);
}

.Popover-message--bottom-left:before,
.Popover-message--bottom-right:before,
.Popover-message--bottom:before {
  border-top-color: var(--white-4);
}

/** Load more button */
.ajax-pagination-form .ajax-pagination-btn {
  background-color: var(--white-1);
  border-color: var(--white-3);
  color: var(--blue-1);
}

.ajax-pagination-form .ajax-pagination-btn:focus,
.ajax-pagination-form .ajax-pagination-btn:hover {
  background-color: var(--white-2);
  color: var(--blue-1);
}

/** Login page. Note that I did deviate from matching the original styles
exactly. Instead I picked values that were close to the original to maintain
the consistent color palette. */
.session-authentication {
  background-color: var(--white-2);
}

.session-authentication .auth-form-header,
.session-authentication .header-logo {
  color: var(--black-1);
}

.session-authentication .auth-form-body {
  background-color: var(--white-1);
  border-color: var(--white-4);
}

.session-authentication .create-account-callout {
  border-color: var(--white-4);
}

/** Tags */
.branch-name {
  background-color: var(--blue-2);
  color: var(--blue-1);
}

a.branch-name {
  color: var(--blue-1);
}

.topic-tag,
.topic-tag-action {
  background-color: var(--blue-3);
}

.topic-tag-link:hover {
  background-color: var(--blue-4);
}

.delete-topic-button,
.delete-topic-link {
  background-color: var(--blue-3);
  border-color: var(--blue-4);
  color: var(--black-2);
}

.delete-topic-button:hover,
.delete-topic-link:hover {
  background-color: var(--blue-4);
}

/** Repo navigation */
.reponav-item {
  color: var(--black-3);
}

.reponav-item.selected {
  background-color: var(--white-1);
  border-left-color: var(--white-3);
  border-right-color: var(--white-3);
  color: var(--black-3);
}

.reponav-item .Counter {
  color: var(--black-1);
}

.reponav-item:focus,
.reponav-item:hover {
  color: var(--black-1);
}

.reponav-item .octicon {
  color: #fff5;
}

/** Sub nav */
.subnav-item {
  border-color: var(--white-3);
  color: var(--black-3);
}

.subnav-item:focus,
.subnav-item:hover {
  background-color: var(--white-2);
}

.subnav-item.selected,
.subnav-item[aria-current],
.subnav-item[aria-selected=true] {
  background-color: #0366d6;
}

/** Repo summary */
.overall-summary {
  border-color: var(--white-6);
}

.numbers-summary li > .nolink,
.numbers-summary li > a {
  color: var(--black-3);
}

.numbers-summary li > a:hover {
  color: var(--blue-1);
}

.numbers-summary .octicon {
  color: var(--black-2);
}

.repository-lang-stats-graph {
  border-color: var(--white-4);
}

.repository-lang-stats ol.repository-lang-stats-numbers li .lang {
  color: var(--black-1);
}

.repository-lang-stats ol.repository-lang-stats-numbers li > a,
.repository-lang-stats ol.repository-lang-stats-numbers li > span {
  color: var(--black-2);
}


.repository-lang-stats-graph .language-color:not(:first-child) {
  border-color: var(--white-1);
}

/** File table */
table.files {
  background-color: var(--white-1);
}

table.files td {
  border-color: var(--white-5);
}

table.files tr.navigation-focus td,
table.files tr[aria-selected=true] td {
  background-color: var(--white-2);
}

table.files td.icon .octicon-file-directory,
table.files td.icon {
  color: var(--black-4);
}

table.files td.message a {
  color: var(--black-2);
}

table.files td.message a:hover {
  color: var(--blue-1);
}

table.files td.age {
  color: var(--black-2);
}

table.files tr.up-tree a:hover {
  background-color: var(--white-6);
}

/** Labels */
.Label {
  color: var(--white-1);
}

.Label--gray {
  background-color: var(--white-5);
  color: var(--black-3);
}

.Label--gray-darker {
  background-color: var(--black-2);
  color: var(--white-1);
}

.Label--outline {
  border-color: var(--black-4);
  color: var(--black-3);
}

/** Use the original colors for yellow labels */
.Label--outline.bg-yellow-light,
.Label.bg-yellow-light {
  color: #586069;
  background-color: #FFF5A9 !important;
  border-color: rgba(27,31,35,.15);
}

/** Markdown */
.markdown-body h1,
.markdown-body h2 {
  border-color: var(--white-5);
}

.markdown-body img {
  background-color: var(--white-1);
}

.markdown-body blockquote {
  border-color: var(--white-6);
  color: var(--black-2);
}

.markdown-body table tr {
  background-color: var(--white-1);
  border-color: var(--white-6);
}

.markdown-body table tr:nth-child(2n) {
  background-color: var(--white-2);
}

.markdown-body table td,
.markdown-body table th {
  border-color: var(--white-6);
}

.markdown-body code,
.markdown-body tt {
  background-color: var(--white-5);
}

.markdown-body hr {
  background-color: var(--white-3);
}

.markdown-body h1 .octicon-link,
.markdown-body h2 .octicon-link,
.markdown-body h3 .octicon-link,
.markdown-body h4 .octicon-link,
.markdown-body h5 .octicon-link,
.markdown-body h6 .octicon-link {
  color: var(--black-2);
}

/** File editor */
.cm-s-github-light.CodeMirror {
  background-color: var(--s-background);
  color: var(--s-ivory);
}

.cm-s-github-light .CodeMirror-lines {
  background-color: var(--s-background);
}

.cm-s-github-light .CodeMirror-gutters,
.cm-s-github-light .CodeMirror-linenumber {
  background-color: var(--s-background);
}

.cm-s-github-light .CodeMirror-gutters,
.cm-s-github-light .CodeMirror-lines {
  border-top: 1px solid var(--white-3);
}

.cm-s-github-light .CodeMirror-linenumber {
  color: var(--black-2);
}

.CodeMirror-line::selection,
.CodeMirror-line > span::selection,
.CodeMirror-line > span > span::selection {
  background-color: var(--black-4)
}

/** File editor syntax highlighting */
.cm-s-github-light .cm-string,
.cm-string-2 {
  color: var(--s-sage);
}

.cm-s-github-light .cm-variable,
.cm-variable-2 {
  color: var(--s-ivory);
}

.cm-property {
  color: var(--s-coral);
}

.cm-operator,
.cm-atom {
  color: var(--s-cyan);
}

.cm-qualifier,
.cm-attribute,
.cm-number {
  color: var(--s-whiskey);
}

.cm-s-github-light .cm-keyword {
  color: var(--s-violet);
}

.cm-tag:not(.cm-bracket) {
  color: var(--s-coral);
}

/** File editor search */
.CodeMirror-dialog-top {
  background-color: var(--white-1);
  border-color: var(--white-3);
}

.CodeMirror-search-hint {
  color: var(--black-3);
}

/** Commit bar */
.commit-tease {
  color: var(--black-3);
}

.commit-tease-sha {
  color: var(--black-4);
}

.ellipsis-expander,
.hidden-text-expander a {
  background-color: var(--white-6);
  color: var(--black-4);
}

.ellipsis-expander:hover,
.hidden-text-expander a:hover {
  background-color: var(--white-4);
}

.ellipsis-expander:active,
.hidden-text-expander a:active {
  color: var(--white-1);
  background-color: var(--blue-1);
}

/** Icons */
.btn-octicon {
  color: var(--black-3);
}

.repohead h1.private .octicon {
  color: var(--black-2);
}

/** Tooltips */
.tooltipped:after {
  background-color: var(--black-1);
  color: var(--white-1);
}

.tooltipped-s:before,
.tooltipped-se:before,
.tooltipped-sw:before {
  border-bottom-color: var(--black-1);
}

.tooltipped-n:before,
.tooltipped-ne:before,
.tooltipped-nw:before {
  border-top-color: var(--black-1);
}

.tooltipped-w:before {
  border-left-color: var(--black-1);
}

/** Commit refs */
.commit-ref {
  background-color: var(--blue-2);
  color: var(--blue-1);
}

.commit-ref .user {
  color: var(--blue-1);
}

/** Progress bar */
.progress-bar {
  background-color: var(--white-5);
}

.progress-bar-inline .progress-bar {
  border-color: var(--white-3);
}

.task-progress .progress-bar {
  background-color: var(--white-3);
}

.task-progress .progress-bar .progress {
  background-color: var(--white-7);
}

.task-progress {
  color: var(--black-3);
}

.Progress {
  background-color: var(--white-3);
}

/** Pagination */
.pagination a,
.pagination em,
.pagination span {
  background: var(--white-1);
  border-color: var(--white-3);
  color: var(--blue-1);
}

.pagination .disabled,
.pagination .disabled:hover,
.pagination .gap,
.pagination .gap:hover,
.pagination [aria-disabled=true],
.pagination [aria-disabled=true]:hover {
  background-color: var(--white-2);
  color: var(--white-4);
}

.pagination a:focus,
.pagination a:hover,
.pagination em:focus,
.pagination em:hover,
.pagination span:focus,
.pagination span:hover {
  background-color: var(--white-5);
  border-color: var(--white-3);
}

/** Issue list */
.issues-reset-query {
  color: var(--black-3);
}

.issues-reset-query:hover {
  color: var(--blue-1);
}

.issue-meta-section .octicon {
  color: var(--white-7);
}

.navigation-focus .AvatarStack-body,
[aria-selected=true] .AvatarStack-body {
  background-color: var(--white-2);
}

/** Tab navigation */
.tabnav {
  border-color: var(--white-4);
}

.tabnav-pr {
  border-color: var(--white-3);
}

.tabnav-tab,
.tabnav-pr .tabnav-tab {
  color: var(--black-3);
}

.tabnav-pr .tabnav-tab.selected {
  border-color: var(--white-3);
  color: var(--black-1);
}

.tabnav-tab.selected,
.tabnav-tab[aria-current],
.tabnav-tab[aria-selected=true] {
  background-color: var(--white-1);
  color: var(--black-1);
  border-color: var(--white-4);
}

.tabnav-tab:focus,
.tabnav-tab:hover {
  color: var(--black-1);
}

@media (max-width: 767px) {
  .tabnav--responsive .tabnav-tab {
    background-color: var(--white-2);
    border-color: var(--white-3);
  }
  
  .tabnav--responsive .tabnav-tab:first-child {
    border-color: var(--white-3);
  }

  .tabnav--responsive .tabnav-tab.selected,
